/** *  */package com.example.toolbar.top;import android.annotation.SuppressLint;import android.app.Activity;import android.content.Context;import android.content.SharedPreferences;import android.util.DisplayMetrics;@SuppressLint("CommitPrefEdits")public class SettingUtil {	public static void SetDisplayMetrics(Activity activity) {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		if (sp.getInt("widthPixels", -1) != -1) {			return;		}		DisplayMetrics display = new DisplayMetrics();		activity.getWindowManager().getDefaultDisplay().getMetrics(display);				SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();		editor.putInt("widthPixels", display.widthPixels);		editor.putInt("heightPixels", display.heightPixels);		editor.commit();	}	public static int getDisplaywidthPixels() {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getInt("widthPixels", -1);	}	public static int getDisplayheightPixels() {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getInt("heightPixels", -1);	}public static void clear(){	SharedPreferences.Editor editor = App			.getIns()			.getSharedPreferences("setting_inf",					Context.MODE_WORLD_WRITEABLE).edit();	editor.clear();	editor.commit();}	public static String getTagString(String tag) {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getString(tag, "");	}	public static int getTagInt(String tag,int defaultTag) {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getInt(tag, defaultTag);	}	public static void setTagString(String tag, String value) {		SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();		editor.putString(tag, value);		editor.commit();	}	public static void setTagInt(String tag, int value) {		SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();		editor.putInt(tag, value);				editor.commit();	}	public static boolean getTagBolean(String tag) {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getBoolean(tag, false);	}	public static void setTagBolean(String tag, boolean value) {		SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();		editor.putBoolean(tag, value);		editor.commit();	}		public static void setPassword(String psw){		SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();				editor.putString("Password", psw);				editor.commit();	}	public static String getPassword(){		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getString("Password", "");	}	public static void setTagBoolean(String tag, boolean value) {		SharedPreferences.Editor editor = App				.getIns()				.getSharedPreferences("setting_inf",						Context.MODE_WORLD_WRITEABLE).edit();		editor.putBoolean(tag, value);		editor.commit();	}	public static boolean getTagBoolean(String tag) {		SharedPreferences sp = App.getIns().getSharedPreferences("setting_inf",				Context.MODE_WORLD_READABLE);		return sp.getBoolean(tag, true);	}		public static int dip2px(float dpValue) {  	        final float scale = App.getIns().getResources().getDisplayMetrics().density;  	        return (int) (dpValue * scale + 0.5f);  	}		 /**      * 根据手机的分辨率�? dp 的单�? 转成�? px(像素)      */      public static int dip2px(Context context, float dpValue) {          final float scale = context.getResources().getDisplayMetrics().density;          return (int) (dpValue * scale + 0.5f);      }        /**      * 根据手机的分辨率�? px(像素) 的单�? 转成�? dp      */      public static int px2dip(Context context, float pxValue) {          final float scale = context.getResources().getDisplayMetrics().density;          return (int) (pxValue / scale + 0.5f);      }      /**     * 将px值转换为sp值，保证文字大小不变     *      * @param pxValue     * @param fontScale     *            （DisplayMetrics类中属�?�scaledDensity�?     * @return     */    public static int px2sp(Context context, float pxValue) {         final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;         return (int) (pxValue / fontScale + 0.5f);     }     /**     * 将sp值转换为px值，保证文字大小不变     *      * @param spValue     * @param fontScale     *            （DisplayMetrics类中属�?�scaledDensity�?     * @return     */     public static int sp2px(Context context, float spValue) {         final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;         return (int) (spValue * fontScale + 0.5f);     } }